// Using ES6 import syntax
import OpenAI from 'openai/index.mjs';

// Initialize the OpenAI configuration with your API key
const openai = new OpenAI({
    apiKey: "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" // Use environment variable for security
 });



// Asynchronous function to send a message to OpenAI
export async function sendMsgToOpenAi(messages) {
    try {
        // Make API call to your backend
        const response = await fetch('/api/openai', { // Use the correct endpoint path to your backend
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ messages }), // Send the messages to your backend
        });

        if (!response.ok) {
            throw new Error("Failed to fetch from backend");
        }

        const data = await response.json(); // Get the response from backend
        return data.text; // Assuming the backend returns { text: "Bot's response" }
    } catch (error) {
        console.error("Error communicating with OpenAI:", error);
        throw error; // Rethrow the error for further handling
    }
}
