// Using ES6 import syntax
import { Configuration, OpenAIApi } from 'openai';

// Initialize the OpenAI configuration with your API key
const configuration = new Configuration({
    apiKey: process.env.OPENAI_API_KEY, // Use environment variable for security
});

const openai = new OpenAIApi(configuration);

// Asynchronous function to send a message to OpenAI
export async function sendMsgToOpenAi(message) {
    try {
        const response = await openai.createChatCompletion({
            model: "gpt-4", // Use the latest model
            messages: [{ role: "user", content: message }],
            max_tokens: 2048,
            temperature: 0.7,
            top_p: 1,
            frequency_penalty: 0,
            presence_penalty: 0,
        });
        return response.data.choices[0].message.content; // Access the content of the response
    } catch (error) {
        console.error("Error communicating with OpenAI:", error);
        throw error; // Rethrow the error for further handling
    }
}
