import { Configuration, OpenAIApi } from 'openai';
const configuration=new Configuration({apiKey:"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"})

const openai= new OpenAIApi(configuration);

export async function sendMsgToOpenAi(messgae){
    const response = await openai.createCompletion({
        model: "text-davinci-003",
        prompt: messgae,
        max_tokens: 2048,
        temperature:0.7,
        top_p:1,
        frequency_penalty: 0,
        presence_penalty:0
    });
    return response.data.choices[0].text;
}
