// Using ES6 import syntax
import OpenAI from 'openai';

// Initialize the OpenAI configuration with your API key
const openai = new OpenAI({
    apiKey: "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",
    dangerouslyAllowBrowser: true  // Use environment variable for security
 });



// Asynchronous function to send a message to OpenAI
export async function sendMsgToOpenAi(message) {
    try {
         // Format the conversation history for OpenAI API
         const formattedMessages = message.map((msg) => ({
            role: msg.isBot ? "assistant" : "user",
            content: msg.text,
        }));

        // Make API call with conversation history
        const response = await openai.chat.completions.create({
            model: "gpt-4o-mini", // Ensure the model name is correct
            messages: formattedMessages,
            max_tokens: 2000,
            temperature: 0.7,
            top_p: 1,
            frequency_penalty: 0,
            presence_penalty: 0,
        });

        console.log(response);
        return response.choices[0].message.content; // Access the content of the response
    } catch (error) {
        console.error("Error communicating with OpenAI:", error);
        throw error; // Rethrow the error for further handling
    }
}
